script:
function isValidDate(date) {
  return date instanceof Date && !isNaN(date.getTime());
}

frappe.ui.form.on('Season', {
	refresh(frm) {
    var today = new Date(); 

    // this is how you get data from form
    var launch_date_start = new Date(frm.doc.launch_date_start); 
    var launch_date_till = new Date(frm.doc.launch_date_till); 
    var selling_date_start = new Date(frm.doc.selling_date_start); 
    var selling_date_till = new Date(frm.doc.selling_date_till);     

    if (isValidDate(launch_date_start)) { 
        var launch_date_start_month =  launch_date_start.toLocaleString('default', { month: 'short' });
    } else {
        var launch_date_start_month =  "";

    } 
    if (isValidDate(launch_date_till)) { 
        var launch_date_till_month= launch_date_till.toLocaleString('default', { month: 'short' });
    } else {
        var launch_date_till_month="";
    }

        if (isValidDate(selling_date_start)) { 
        var selling_date_start_month =  selling_date_start.toLocaleString('default', { month: 'short' });
    } else {
        var selling_date_start_month =  "";

    } 
    if (isValidDate(selling_date_till)) { 
        var selling_date_till_month= selling_date_till.toLocaleString('default', { month: 'short' });
    } else {
        var selling_date_till_month="";
    }

    var launch_period = launch_date_start_month + "-" + launch_date_till_month ;
    var selling_period = selling_date_start_month + "-" + selling_date_till_month;

   // var age = today.getFullYear() - birthDate.getFullYear(); var m = today.getMonth() - birthDate.getMonth(); 
//    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) { 
 //     age--; 
   // } 

    if (frm.doc.specific_year === 1) {
            // Checkbox is checked
            //frappe.msgprint("The checkbox is checked!");
            var yearValue = Math.max((launch_date_start.getFullYear() || 0), (launch_date_till.getFullYear() || 0), (selling_date_start.getFullYear() || 0) , (selling_date_till.getFullYear() || 0) );
            //frappe.msgprint("JS The checkbox is checked!" +yearValue );
            frm.set_value('season_year', yearValue);
            yearValue = ' ' + yearValue;
        } else {
            // Checkbox is not checked
            //frappe.msgprint("The checkbox is not checked.");
            var yearValue = '';
        }
    // use frm.set_value to set value of a field
    frm.set_value('launch_period', launch_period + yearValue);
    frm.set_value('selling_period', selling_period + yearValue);    
	}
})

frappe.ui.form.on('Product Launch', {
	refresh(frm) {

            frm.add_custom_button(__("Create Project"), function() {
                //frappe.msgprint("JS Create Prokject");
                // Call the server-side function here
                frappe.call({

                    method:"addproject",
                    args: {
                        name: frm.doc.name
                    },
                    callback: function(r) {
                        if(r.message) {
                            frappe.msgprint("Task created successfully!");
                        }
                    }
                });                

            });

            frm.add_custom_button(__("Create Pong"), function() {
                //frappe.msgprint("JS Create Prokject");
                // Call the server-side function here
                frappe.call({

                    method:"demo",
                    args: {
                        message:"ping"
                    },
                    callback: function(r) {
                        if(r.message) {
                            frappe.msgprint("PINGPONG created successfully!");
                        }
                    }
                });                

            });            
	}
})

frappe.ui.form.on('Size Groups', {
    refresh: function(frm) {
        frm.doc.size_.forEach(size_row => {
            size_row.size_group_code = frm.doc.size_group_code; 
        });
        refresh_field('size'); 
    },

    size_group_code: function(frm) { // Trigger when 'customer' field in parent changes
        frm.doc.size_.forEach(size_row => {
            item_row.customer_name = frm.doc.customer; 
        });
        refresh_field('size');
    }
});

frappe.ui.form.on('Product Launch', {

  measure_category: function(frm){

  let measure_category = frm.doc.measure_category;

   if(measure_category){
    frappe.call({
     method: "product_lifecycle_management.product_lifecycle_management.api.get_measure_attributes",
     args: {measure_category: measure_category}
    }).done((r) => {

   frm.doc.measures = []

   $.each(r.message, function(_i, e){
   let entry = frm.add_child("measures");
   entry.measurement_attribute = e.name;
 })
 refresh_field("measures")

  })
 }

}

})
